mapper.select:
select DATA_TYPE, data_count, IRSL_DATE
from (select DATA_TYPE,
data_count,
IRSL_DATE,
row_number() over(partition by DATA_TYPE,IRSL_DATE order by OP_DATE desc) as rn
from SJPT_REALTIME_STATIC_DAY
where IRSL_DATE between #{startDay} and #{endDay}
   and DATA_TYPE in(1,2,3,22)  
) t
where t.rn = 1

select DATA_TYPE, data_count, IRSL_DATE,OP_DATE
from (select DATA_TYPE,
        data_count,
        IRSL_DATE,
        OP_DATE,
        row_number() over(partition by DATA_TYPE order by OP_DATE desc) as rn
        from SJPT_REALTIME_STATIC_DAY
        where IRSL_DATE = #{day}

        and DATA_TYPE in

            #{item}


    ) t
where t.rn = 1
order by DATA_TYPE

select DATA_TYPE, data_count, IRSL_DATE,OP_DATE
from (select DATA_TYPE,
        data_count,
        IRSL_DATE,
        OP_DATE,
        row_number() over(partition by DATA_TYPE order by OP_DATE desc) as rn
        from SJPT_REALTIME_STATIC_DAY
        where IRSL_DATE = #{yesterDay}

        and DATA_TYPE in

            #{item}


    ) t
where t.rn = 1
order by DATA_TYPE

select DATA_TYPE, data_count, IRSL_DATE,OP_DATE
from (select DATA_TYPE,
data_count,
IRSL_DATE,
OP_DATE,
row_number() over(partition by DATA_TYPE order by OP_DATE desc) as rn
from SJPT_REALTIME_STATIC_DAY
where IRSL_DATE = #{day}

    and DATA_TYPE in

        #{item}


) t
where t.rn = 1
order by DATA_TYPE

select * from (select a.DATA_TYPE, b.IRSL_DATE, b.data_count, a.datahour, b.op_date, row_number() over(partition by a.DATA_TYPE order by b.OP_DATE desc) as rn from (select DATA_TYPE, max(op_date) op_date, to_char(max(op_date), 'HH24') as datahour from SJPT_REALTIME_STATIC_DAY where IRSL_DATE = #{day}
and DATA_TYPE in
#{item}
group by DATA_TYPE) a left join (select DATA_TYPE, data_count, IRSL_DATE, op_date, to_char(op_date, 'HH24') as datahour from SJPT_REALTIME_STATIC_DAY where IRSL_DATE = #{yesterDay}
and DATA_TYPE in
#{item}
) b on a.DATA_TYPE = b.DATA_TYPE and a.datahour >= b.datahour ) c
where c.rn = 1
order by DATA_TYPE

select * from (select a.DATA_TYPE, b.IRSL_DATE, b.data_count, a.datahour, b.op_date, row_number() over(partition by a.DATA_TYPE order by b.OP_DATE desc) as rn from (select DATA_TYPE, max(op_date) op_date, to_char(max(op_date), 'HH24') as datahour from SJPT_REALTIME_STATIC_DAY where IRSL_DATE = #{day}
and DATA_TYPE in
#{item}
group by DATA_TYPE) a left join (select DATA_TYPE, data_count, IRSL_DATE, op_date, to_char(op_date, 'HH24') as datahour from SJPT_REALTIME_STATIC_DAY where IRSL_DATE = #{yesterDay}
and DATA_TYPE in
#{item}
) b on a.DATA_TYPE = b.DATA_TYPE and a.datahour >= b.datahour ) c
where c.rn = 1
order by DATA_TYPE

SELECT DATA_TYPE, DATA_COUNT, IRSL_DATE, OP_DATE FROM (SELECT ID, DATA_TYPE, DATA_COUNT, IRSL_DATE, OP_DATE, ROW_NUMBER() OVER(PARTITION BY DATA_TYPE, IRSL_DATE ORDER BY OP_DATE DESC) AS DT_ORDER FROM SJPT_REALTIME_STATIC_DAY T WHERE T.IRSL_DATE BETWEEN #{preDay}AND #{day}
and t.DATA_TYPE in
#{item}
) X WHERE X.DT_ORDER = 1

SELECT #{dataType} as DATA_TYPE, sum(DATA_COUNT)as DATA_COUNT, IRSL_DATE FROM (SELECT ID, DATA_TYPE, DATA_COUNT, IRSL_DATE, OP_DATE, ROW_NUMBER() OVER(PARTITION BY DATA_TYPE, IRSL_DATE ORDER BY OP_DATE DESC) AS DT_ORDER FROM SJPT_REALTIME_STATIC_DAY T WHERE T.IRSL_DATE BETWEEN #{preDay}AND #{day}
and t.DATA_TYPE in
#{item}
) X WHERE X.DT_ORDER = 1 group by IRSL_DATE order by IRSL_DATE

select DATA_TYPE, data_count, IRSL_DATE,OP_DATE from (select DATA_TYPE, data_count, IRSL_DATE, OP_DATE, row_number() over(partition by DATA_TYPE order by IRSL_DATE desc) as rn from SJPT_REALTIME_STATIC_DAY
where DATA_TYPE in
#{item}
) t where t.rn = 1 order by DATA_TYPE

select t.DATA_TYPE,t.DATA_COUNT,t.IRSL_DATE,t.op_date from (select DATA_TYPE,DATA_COUNT,IRSL_DATE,op_date, row_number() over(partition by data_type order by IRSL_DATE desc) as rn from SJPT_REALTIME_STATIC_DAY
data_type in
#{item}
) t where t.rn = 2
