mapper.select:
select h.DATA_TYPE,h.DATA_COUNT,h.IRSL_DATE,h.DATA_DATE from
       (select t.*,
                row_number() over(PARTITION BY data_type order by Data_date desc) as rn
        from sjpt_realtime_STATIC_month t
        where t.irsl_date=#{month}

            and data_type in

                #{item}


        and Data_date like #{day}||'%'
       ) h
       where h.rn = 1

select * from(
  select t.DATA_TYPE,t.DATA_COUNT,t.IRSL_DATE,t.DATA_DATE,
    row_number() over( order by IRSL_DATE desc ) as rn
 from
 (select DATA_TYPE,DATA_COUNT,IRSL_DATE,DATA_DATE,
 row_number() over(partition by irsl_date order by DATA_DATE desc ) as rn
 from  sjpt_realtime_STATIC_month
 where data_type= #{type}
 )  t
 where t.rn=1
 order by irsl_date
  )f
  where f.rn<=#{months}

select t.irsl_date from ( select r.irsl_date, row_number() over( order by irsl_date desc) as rn from sjpt_realtime_STATIC_month r
r.data_type in
#{item}
) t where t.rn=1

select DATA_TYPE,DATA_COUNT,IRSL_DATE,DATA_DATE from sjpt_realtime_STATIC_month where data_type = #{type}
and irsl_date in
#{item}
order by irsl_date

select aa.DATA_TYPE,aa.DATA_COUNT,aa.IRSL_DATE,aa.DATA_DATE from (select DATA_TYPE,DATA_COUNT,IRSL_DATE,DATA_DATE, row_number() over(partition by irsl_date order by DATA_DATE desc ) as rn from sjpt_realtime_STATIC_month where data_type = #{type}
and irsl_date in
#{item}
) aa where aa.rn = 1
