mapper.select:
select t.DATA_TYPE,t.DATA_COUNT,t.IRSL_DATE,t.DATA_DATE from (select r.DATA_TYPE,r.DATA_COUNT,r.IRSL_DATE,r.DATA_DATE, row_number() over(partition by data_type order by DATA_DATE desc) as rn from SJPT_REALTIME_STATIC_WEEK r) t
t.data_type in
#{item}
and t.rn = #{flag}

select t.irsl_date from ( select r.irsl_date, row_number() over( order by irsl_date desc) as rn from sjpt_realtime_static_week r
r.data_type in
#{item}
) t where t.rn=1

select DATA_TYPE,DATA_COUNT,IRSL_DATE,DATA_DATE from sjpt_realtime_static_week where data_type = #{type}
and irsl_date in
#{item}
order by irsl_date

select aa.DATA_TYPE,aa.DATA_COUNT,aa.IRSL_DATE,aa.DATA_DATE from ( select DATA_TYPE,DATA_COUNT,IRSL_DATE,DATA_DATE, row_number() over(partition by irsl_date order by DATA_DATE desc ) as rn from sjpt_realtime_static_week where data_type = #{type}
and irsl_date in
#{item}
order by irsl_date) aa where aa.rn = 1
