select DATA_TYPE, data_count, IRSL_DATE_H from (select DATA_TYPE, data_count, IRSL_DATE_H, row_number() over(partition by DATA_TYPE order by IRSL_DATE_H desc) as rn from SJPT_REALTIME_STATIC_HOUR where IRSL_DATE_H between concat(#{day}, '00') and concat(#{day}, '23')
and DATA_TYPE in
#{item}
) t where t.rn = 1 order by DATA_TYPE

select DATA_TYPE, data_count,IRSL_DATE_H from(select DATA_TYPE, data_count, SUBSTR(IRSL_DATE_H,0,8) AS IRSL_DATE_H, row_number() over(partition by DATA_TYPE,SUBSTR(IRSL_DATE_H, 0, 8) order by IRSL_DATE_H desc) as rn from SJPT_REALTIME_STATIC_HOUR where IRSL_DATE_H between concat(#{preDay}, '00') and concat(#{day}, '23')
and DATA_TYPE in
#{item}
) t where t.rn = 1 order by DATA_TYPE,IRSL_DATE_H

select DATA_TYPE, data_count, IRSL_DATE_H,OP_DATE from (select DATA_TYPE, data_count, IRSL_DATE_H, OP_DATE, row_number() over(partition by DATA_TYPE order by IRSL_DATE_H desc) as rn from SJPT_REALTIME_STATIC_HOUR where IRSL_DATE_H between concat(#{yesterDay},'00') and concat(#{yesterDay},'23')
and DATA_TYPE in
#{item}
) t where t.rn = 1 order by DATA_TYPE

select DATA_TYPE, data_count, IRSL_DATE_H from (select DATA_TYPE, data_count, IRSL_DATE_H, row_number() over(partition by DATA_TYPE,substr(IRSL_DATE_H,0,8) order by IRSL_DATE_H desc) as rn from SJPT_REALTIME_STATIC_HOUR where IRSL_DATE_H between concat(#{preday}, '00') and concat(#{day}, '23')
and DATA_TYPE in
#{item}
) t where t.rn = 1 order by DATA_TYPE ,IRSL_DATE_H asc

